mapper.insert:
INSERT zyyj_course_exam VALUES(
	(select course_id from zyyj_course where name=#{course}), 
	#{exam_id});

INSERT zyyj_exam_record VALUES(
	(select user_id from zyyj_user where realname=#{creator}), 
	#{exam_id});

mapper.update:
UPDATE zyyj_exam SET 
	name=#{name}, total_question=#{total_question}, 
	total_score=#{total_score}, pass_score=#{pass_score}, 
	question_num=#{question_num}, limit_time=#{limit_time}, start_time=#{start_time} 
WHERE exam_id=#{exam_id};

UPDATE zyyj_course_exam SET 
	course_id=(select course_id from zyyj_course where name=#{course}) 
WHERE exam_id=#{exam_id};
